import { Component, OnInit } from '@angular/core';
import { Contacto } from 'src/app/models/contacto';
import { ContactoService } from 'src/app/services/contacto.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-admin-contactos',
  templateUrl: './admin-contactos.component.html',
  styleUrls: ['./admin-contactos.component.css']
})
export class AdminContactosComponent implements OnInit {
  public cargando:boolean = false;
  public mensajeError:string  | null= null;
  contactos$!: Observable<Contacto[]>;

  constructor(private servicioContactos:ContactoService ) { }

  ngOnInit(): void {
    this.cargando = true;
    this.contactos$ = this.servicioContactos.obtenerContactos();
  }
}
